#
# Table structure for table 'race'
#
CREATE TABLE race (
  raceid smallint(5) unsigned NOT NULL auto_increment,
  name varchar(60),
  nickname varchar(12) DEFAULT '' NOT NULL,
  raceday varchar(10) DEFAULT '' NOT NULL,
  organizer varchar(30),
  homehref varchar(50),
  place varchar(20),
  typenfee varchar(40),
  userid varchar(12),
  modifier varchar(12),
  racetime varchar(5),
  etc varchar(200),
  inviting char(1),
  PRIMARY KEY (raceid),
  UNIQUE racenickname (nickname)
);

